import react, { useState, useEffect } from 'react'
import Doughnut from './doughnut'
import Label from './Label'
import Form from './form'
import Expenses from './prevExpenses'
import axios from 'axios'

// const data = [{ name: 'Investments', color: 'pink', amount: 20 }, { name: 'Savings', color: 'cyan', amount: 50 },
// { name: 'Expenenses', color: 'red', amount: 60 }, { name: 'Other', color: 'yellow', amount: 10 },]

// const Labels = data.map((items) => <Label name={items.name} color={items.color} amount={items.amount} />)

export default function Home() {
  const [showForm, setShowForm] = useState(false)
  const [btnText, setBtntext] = useState('Add New Expense')
  const [edata, setEdata] = useState([])
  const [expenses, setExpenses] = useState([])

  async function fetchData() {
    try {
      const res = await axios.get('http://localhost:8080/expenses');
      return res.data;
    } catch (err) {
      console.log(err);
    }

  }

  useEffect(() => {
    fetchData()
      .then(data => {
        setEdata(data)
      })
      var Other = 0;
      var Investments = 0
      let Expenses = 0
      let Savings = 0
      for (let i = 0; i < edata.length; i++) {
        console.log(edata[i].eType)
        if (edata[i].eType === 'other') {
          Other += edata[i].eAmount;
        }
        else if (edata[i].eType === 'investments') {
          Investments += edata[i].eAmount;
        }
        else if (edata[i].eType === 'expenses') {
          Expenses += edata[i].eAmount;
        }
        else {
          Savings += edata[i].eAmount;
        }
      }
  }, [])
  console.log(edata)

  var Other = 0;
  var Investments = 0
  let Expenses = 0
  let Savings = 0
  for (let i = 0; i < data.length; i++) {
    console.log(data[i].eType)
    if (data[i].eType === 'other') {
      Other += data[i].eAmount;
    }
    else if (data[i].eType === 'investments') {
      Investments += data[i].eAmount;
    }
    else if (data[i].eType === 'expenses') {
      Expenses += data[i].eAmount;
    }
    else {
      Savings += data[i].eAmount;
    }
  }


  const e = edata.map((items) => <Expenses name={items.eName} />)
  function toggleForm() {
    console.log("pressed")
    setShowForm(prev => !prev);
    if (!showForm) {
      setBtntext('Done')
    }
    else {
      setBtntext('Add New Expense')
    }
  }
  return (
    <div className='home'>
      <div className='combo'>
        <Doughnut data={edata} />
        <div className='label-container'>
          {Labels}
        </div>
      </div>
      <div className="r-box">
        {!showForm ? e : null}
        {showForm ? <Form /> : null}
        <button className="btn" onClick={toggleForm}>{btnText}</button>
      </div>

    </div>
  )
}
