import React, { useState } from 'react';

export default function GoalsForm() {
  const [currentField, setCurrentField] = useState('investment');
  const [investment, setInvestment] = useState(0);
  const [expenses, setExpenses] = useState(0);
  const [savings, setSavings] = useState(0);
  const [other, setOther] = useState(0);

  function handleSubmit() {
    switch (currentField) {
      case 'investment':
        setCurrentField('expenses');
        break;
      case 'expenses':
        setCurrentField('savings');
        break;
      case 'savings':
        setCurrentField('other');
        break;
      case 'other':
        // Submit the final form here
        break;
      default:
        break;
    }
  }

  return (
    <div className="goals-form">
      {currentField === 'investment' && (
        <div className="investment-section">
          <h2 className="section-title">Investment</h2>
          <input
            className="input-field"
            type="number"
            value={investment}
            onChange={e => setInvestment(e.target.value)}
          />
          <button className="next-button" onClick={handleSubmit}>Next</button>
        </div>
      )}
      {currentField === 'expenses' && (
        <div className="expenses-section">
          <h2 className="section-title">Expenses</h2>
          <input
            className="input-field"
            type="number"
            value={expenses}
            onChange={e => setExpenses(e.target.value)}
          />
          <button className="next-button" onClick={handleSubmit}>Next</button>
        </div>
      )}
      {currentField === 'savings' && (
        <div className="savings-section">
          <h2 className="section-title">Savings</h2>
          <input
            className="input-field"
            type="number"
            value={savings}
            onChange={e => setSavings(e.target.value)}
          />
          <button className="next-button" onClick={handleSubmit}>Next</button>
        </div>
      )}
      {currentField === 'other' && (
        <div className="other-section">
          <h2 className="section-title">Other</h2>
          <input
            className="input-field"
            type="number"
            value={other}
            onChange={e => setOther(e.target.value)}
          />
          <button className="submit-button" onClick={handleSubmit}>Submit</button>
        </div>
      )}
    </div>
  );
}
