import React, { useState } from 'react';

export default function GoalsForm() {
  const [currentField, setCurrentField] = useState('investment');
  const [investment, setInvestment] = useState(0);
  const [expenses, setExpenses] = useState(0);
  const [savings, setSavings] = useState(0);
  const [other, setOther] = useState(0);

  function handleSubmit() {
    switch (currentField) {
      case 'investment':
        setCurrentField('expenses');
        break;
      case 'expenses':
        setCurrentField('savings');
        break;
      case 'savings':
        setCurrentField('other');
        break;
      case 'other':
        // Submit the final form here
        window.location.reload();
        break;
      default:
        break;
    }
  }

  return (
    <div>
      {currentField === 'investment' && (
        <div>
          <h2>Investment</h2>
          <input
            type="number"
            value={investment}
            onChange={e => setInvestment(e.target.value)}
          />
          <button onClick={handleSubmit}>Next</button>
        </div>
      )}
      {currentField === 'expenses' && (
        <div>
          <h2>Expenses</h2>
          <input
            type="number"
            value={expenses}
            onChange={e => setExpenses(e.target.value)}
          />
          <button onClick={handleSubmit}>Next</button>
        </div>
      )}
      {currentField === 'savings' && (
        <div>
          <h2>Savings</h2>
          <input
            type="number"
            value={savings}
            onChange={e => setSavings(e.target.value)}
          />
          <button onClick={handleSubmit}>Next</button>
        </div>
      )}
      {currentField === 'other' && (
        <div>
          <h2>Other</h2>
          <input
            type="number"
            value={other}
            onChange={e => setOther(e.target.value)}
          />
          <button onClick={handleSubmit}>Submit</button>
        </div>
      )}
    </div>
  );
}
