import React, { useState, useEffect } from 'react'
import NavbarSettings from "./navbar_settings"
import axios from 'axios'

export default function Accounts() {
  const [accounts, setAccounts] = useState([])
  const [num, setNum] = useState('Loading...')
  const [name, setName] = useState('Loading...')
  const [type, setType] = useState('Loading...')
  const [balance, setBalance] = useState('Loading...')

  async function fetchdata() {
    try {
      const res = await axios.get('http://localhost:8080/accounts/')
      console.log(res)
      return res.data
    } catch (error) {
      console.log(error)
    }
  }

  useEffect(() => {
    fetchdata().then(datas => {
      setAccounts(datas)
      if (accounts.length > 0) {
        setNum(accounts[0].acc_no)
        setName(accounts[0].acc_name)
        setType(accounts[0].acc_type)
        setBalance(accounts[0].acc_balance)
      }
    })
  }, [accounts])

  console.log(accounts)

  return (
    <div className="acc">
      <div>
        <h3>Settings</h3>
        <NavbarSettings />
      </div>
      <div className="acc-intro">
        <h4>Account Number</h4>
        {num}
        <h4>Account Name</h4>
        {name}
        <h4>Account Type</h4>
        {type}
        <h4>Account Balance</h4>
        {balance}
      </div>
    </div>
  )
}
